select * from Finance_data;
--Finding Average Investments
select avg(Mutual_funds + Equity_market + Debentures + Government_Bonds + Fixed_Deposits + PPF + Gold) as Average_Returns
from Finance_data; 

--Investments by Gender
SELECT 
    gender,
    AVG(Mutual_Funds) AS Avg_Mutual_Funds,
    AVG(Equity_Market) AS Avg_Equity_Market,
    AVG(Debentures) AS Avg_Debentures,
    AVG(Government_Bonds) AS Avg_Government_Bonds,
    AVG(Fixed_Deposits) AS Avg_Fixed_Deposits,
    AVG(PPF) AS Avg_PPF,
    AVG(Gold) AS Avg_Gold
FROM 
    Finance_Data
GROUP BY 
    gender;

--Cumulative Average Investments by Age 
SELECT 
    age,
    AVG(Mutual_Funds) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Mutual_Funds,
    AVG(Equity_Market) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Equity_Market,
    AVG(Debentures) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Debentures,
    AVG(Government_Bonds) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Government_Bonds,
    AVG(Fixed_Deposits) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Fixed_Deposits,
    AVG(PPF) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_PPF,
    AVG(Gold) OVER (PARTITION BY age ORDER BY age) AS Cumulative_Avg_Gold
FROM 
    Finance_Data
ORDER BY 
    age;


-- Pivot table for Investment Avenues by Gender and Objective
SELECT 
    gender,
